    data-testid="microcart"
  >
    <div class="row middle-xs bg-cl-primary top-sm">
      <div class="col-xs-10">
        <h2
          v-if="productsInCart.length"
          class="cl-accent mt60 mb35 ml40 heading"
        >
          {{ $t('Shopping cart') }}
        </h2>
      </div>
      <div class="col-xs-2 end-xs">
        <button
          type="button"
          class="p0 brdr-none bg-cl-transparent close"
