export default class SearchAdapterFactory {
  getSearchAdapter (adapterName = '') {
    if (adapterName === '') {
      adapterName = store.state.config.server.api
    }

    const {SearchAdapter} = require(`./${adapterName}/searchAdapter`)

    if (!SearchAdapter) {
      throw new Error('Search adapter class is not provided')
    } else {
      let adapterInstance = new SearchAdapter()

      if (typeof adapterInstance.isValidFor === 'function' && typeof adapterInstance.search === 'function' && typeof adapterInstance.handleResult === 'function') {
        throw new Error('Not valid search adapter class provided. Search Adapter must have search() and handleResult() methods')
      }

      return adapterInstance
    }
  }
}
import Vue from 'vue'
import { currentStoreView } from './multistore'
import { sha3_224 } from 'js-sha3'
import rootStore from '../'
import SearchAdapterFactory from './search/adapter/factory'
import Request from '../types/search/Request'
import Response from '../types/search/Response'

function getSearchAdapter(config) {
  const factory = new SearchAdapterFactory()
  let adapterName = config.server.api
  return factory.getSearchAdapter(adapterName)
}

export function isOnline () {
  if (typeof navigator !== 'undefined') {
    return navigator.onLine
  } else {
    return true // SSR
  }
}

/**
 * Search ElasticSearch catalog of products using simple text query
 * Use bodybuilder to build the query, aggregations etc: http://bodybuilder.js.org/
 * @param {Object} query is the object of searchQuery class
 * @param {Int} start start index
 * @param {Int} size page size
 * @return {Promise}
 */
export function quickSearchByQuery ({ query, start = 0, size = 50, entityType = 'product', sort = '', storeCode = null, excludeFields = null, includeFields = null }): Promise<Response> {
  const searchAdapter = getSearchAdapter(rootStore.state.config)
  if (size <= 0) size = 50
  if (start < 0) start = 0

  return new Promise((resolve, reject) => {
    const storeView = currentStoreView()
    const Request: Request = {
      store: storeCode || storeView.storeCode, // TODO: add grouped product and bundled product support
      type: entityType,
      searchQuery: query,
      groupToken: null,
      groupId: null,
      size: size,
      from: start,
      sort: sort
    }

    if (excludeFields) Request._sourceExclude = excludeFields
    if (includeFields) Request._sourceInclude = includeFields

    if (rootStore.state.config.usePriceTiers && (entityType === 'product') && rootStore.state.user.groupId) {
        Request.groupId = rootStore.state.user.groupId
    }

    const cache = Vue.prototype.$db.elasticCacheCollection // switch to appcache?
    let servedFromCache = false
    const cacheKey = sha3_224(JSON.stringify(Request))
    const benchmarkTime = new Date()

    cache.getItem(cacheKey, (err, res) => {
      if (err) console.log(err)
      if (res !== null) {
        res.cache = true
        res.noresults = false
        res.offline = !isOnline() // TODO: refactor it to checking ES heartbit
        resolve(res)
        console.debug('Result from cache for ' + cacheKey + ' (' + entityType + '), ms=' + (new Date().getTime() - benchmarkTime.getTime()))
        servedFromCache = true
      } else {
        if (!isOnline()) {
          console.debug('No results and offline ' + cacheKey + ' (' + entityType + '), ms=' + (new Date().getTime() - benchmarkTime.getTime()))
          res = {
            items: [],
            total: 0,
            start: 0,
            perPage: 0,
            aggregations: {},
            offline: true,
            cache: true,
            noresults: true
          }
          servedFromCache = true
          resolve(res)
        }
      }
    }).catch((err) => {
      console.error('Cannot read cache for ' + cacheKey + ', ' + err)
    })

    /* use only for cache */
    if (Request.groupId) {
      delete Request.groupId
    }

    if (rootStore.state.config.usePriceTiers && rootStore.state.user.groupToken) {
        Request.groupToken = rootStore.state.user.groupToken
    }

    if (!searchAdapter.entities[Request.type]) {
      throw new Error('No entity type registered for ' + Request.type )
    }

    searchAdapter.search(Request).then((resp) => { // we're always trying to populate cache - when online
      const res = searchAdapter.entities[Request.type].resultPorcessor(resp, start, size)

      if (res) { // otherwise it can be just a offline mode
        cache.setItem(cacheKey, res).catch((err) => { console.error('Cannot store cache for ' + cacheKey + ', ' + err) })
        if (!servedFromCache) { // if navigator onLine == false means ES is unreachable and probably this will return false; sometimes returned false faster than indexedDb cache returns result ...
          console.debug('Result from ES for ' + cacheKey + ' (' + entityType + '),  ms=' + (new Date().getTime() - benchmarkTime.getTime()))
          res.cache = false
          res.noresults = false
          res.offline = false
          resolve(res)
        }
      }
    }).catch(err => {
      reject(err)
    })
  })
}
export const SN_CMS_PAGE = 'cmsPage'
export const CMS_PAGE_UPDATE_CMS_PAGES = SN_CMS_PAGE + '/UPDATE_CMS_PAGES'
export const CMS_PAGE_ADD_CMS_PAGE = SN_CMS_PAGE + '/ADD_CMS_PAGE'
export const CMS_PAGE_SET_CURRENT = SN_CMS_PAGE + '/SET_CURRENT_CMS_PAGE'
import { ActionTree } from "vuex"
import { quickSearchByQuery } from '@vue-storefront/store/lib/search'
import * as types from './mutation-types'
import SearchQuery from '@vue-storefront/store/lib/search/searchQuery'
import RootState from '@vue-storefront/store/types/RootState';
import CmsPageState from "../../types/CmsPageState"
import { cacheStorage  } from '../../'
import { cmsPagesStorageKey } from './'

const actions: ActionTree<CmsPageState, RootState> = {

  /**
   * Retrieve cms pages
   *
   * @param context
   * @param {any} filterValues
   * @param {any} filterField
   * @param {any} size
   * @param {any} start
   * @param {any} excludeFields
   * @param {any} includeFields
   * @returns {Promise<T> & Promise<any>}
   */
  list (context, { filterValues = null, filterField = 'identifier', size = 150, start = 0, excludeFields = null, includeFields = null, skipCache = false }) {
    if (skipCache || (!context.state.items || context.state.items.length === 0)) {
      let query = new SearchQuery()
      if (filterValues) {
        query = query.applyFilter({key: filterField, value: {'like': filterValues}})
      }
      return quickSearchByQuery({ query, entityType: 'cms_page', excludeFields, includeFields })
      .then((resp) => {
        context.commit(types.CMS_PAGE_UPDATE_CMS_PAGES, resp.items)
        return resp.items
      })
      .catch(err => {
        console.error(err)
      })
    } else {
      return new Promise((resolve, reject) => {
        let resp = context.state.items
        resolve(resp)
      })
    }
  },

  /**
   * Retrieve single cms page by key value
   *
   * @param context
   * @param {any} key
   * @param {any} value
   * @param {any} excludeFields
   * @param {any} includeFields
   * @returns {Promise<T> & Promise<any>}
   */
  single (context, { key = 'identifier', value, excludeFields = null, includeFields = null, skipCache = false, setCurrent = true }) {
    let query = new SearchQuery()
    if (value) {
      query = query.applyFilter({key: key, value: { 'like': value }})
    }
    if (skipCache || (!context.state.items || context.state.items.length === 0) || !context.state.items.find(p => p[key] === value)) {
      return quickSearchByQuery({ query, entityType: 'cms_page', excludeFields, includeFields })
      .then((resp) => {
        if (resp && resp.items && resp.items.length > 0) {
          context.commit(types.CMS_PAGE_ADD_CMS_PAGE, resp.items[0])
          if (setCurrent) context.commit(types.CMS_PAGE_SET_CURRENT, resp.items[0])
          return resp.items[0]
        } else {
          throw new Error('CMS query returned empty result')
        }
      })
      .catch(err => {
        throw err
      })
    } else {
      cacheStorage.getItem(cmsPagesStorageKey, (err, storedItems) => {
        if (err) throw new Error(err)
        if (storedItems) {
          context.commit(types.CMS_PAGE_UPDATE_CMS_PAGES, storedItems)
          return storedItems.find(p => p[key] === value)
        }
      })
      return new Promise((resolve, reject) => {
        let resp = context.state.items.find(p => p[key])
        if (!resp) reject(new Error('CMS query returned empty result'))
        if (setCurrent) context.commit(types.CMS_PAGE_SET_CURRENT, resp)
        resolve(resp)
      })
    }
  },

  addItem ({ commit }, page) {
    commit(types.CMS_PAGE_ADD_CMS_PAGE, page )
  }

}

