import Vue from 'vue'
import { ActionTree } from 'vuex'
import * as types from './mutation-types'
import { quickSearchByQuery } from '@vue-storefront/store/lib/search'
import { entityKeyName } from '@vue-storefront/store/lib/entities'
import rootStore from '@vue-storefront/store'
import i18n from '@vue-storefront/i18n'
import chunk from 'lodash-es/chunk'
import trim from 'lodash-es/trim'
import toString from 'lodash-es/toString'
import { optionLabel } from '../../helpers/optionLabel'
import RootState from '@vue-storefront/store/types/RootState'
import CategoryState from '../../types/CategoryState'
import SearchQuery from '@vue-storefront/store/lib/search/searchQuery'
import { currentStoreView } from '@vue-storefront/store/lib/multistore'

const actions: ActionTree<CategoryState, RootState> = {
  /**
   * Reset current category and path
   * @param {Object} context
   */
  reset (context) {
    context.commit(types.CATEGORY_UPD_CURRENT_CATEGORY_PATH, [])
    context.commit(types.CATEGORY_UPD_CURRENT_CATEGORY, {})
    rootStore.dispatch('stock/clearCache')
    Vue.prototype.$bus.$emit('category-after-reset', { })
  },
  /**
   * Load categories within specified parent
   * @param {Object} commit promise
   * @param {Object} parent parent category
   */
  list (context, { parent = null, onlyActive = true, onlyNotEmpty = false, size = 4000, start = 0, sort = 'position:asc', includeFields = rootStore.state.config.entities.optimize ? rootStore.state.config.entities.category.includeFields : null, skipCache = false }) {
    const commit = context.commit

    let searchQuery = new SearchQuery()
    if (parent && typeof parent !== 'undefined') {
      searchQuery = searchQuery.applyFilter({key: 'parent_id', value: {'eq': parent.id}})
    }

    if (onlyActive === true) {
      searchQuery = searchQuery.applyFilter({key: 'is_active', value: {'eq': true}})
    }

    if (onlyNotEmpty === true) {
      searchQuery = searchQuery.applyFilter({key: 'product_count', value: {'gt': 0}})
    }

    if (skipCache || (!context.state.list || context.state.list.length === 0)) {
      return quickSearchByQuery({ entityType: 'category', query: searchQuery, sort: sort, size: size, start: start, includeFields: includeFields }).then((resp) => {
        commit(types.CATEGORY_UPD_CATEGORIES, resp)
        Vue.prototype.$bus.$emit('category-after-list', { query: searchQuery, sort: sort, size: size, start: start, list: resp })
        return resp
      })
    } else {
      return new Promise((resolve, reject) => {
        let resp = { items: context.state.list, total: context.state.list.length }
        Vue.prototype.$bus.$emit('category-after-list', { query: searchQuery, sort: sort, size: size, start: start, list: resp })
        resolve(resp)
      })
    }
  },

  /**
   * Load category object by specific field - using local storage/indexed Db
   * loadCategories() should be called at first!
   * @param {Object} commit
   * @param {String} key
   * @param {String} value
   * @param {Bool} setCurrentCategory default=true and means that state.current_category is set to the one loaded
   */
  single (context, { key, value, setCurrentCategory = true, setCurrentCategoryPath = true,  populateRequestCacheTags = true }) {
    const state = context.state
    const commit = context.commit
    const dispatch = context.dispatch

    return new Promise((resolve, reject) => {
      let setcat = (error, mainCategory) => {
        if (error) {
          console.error(error)
          reject(error)
        }

        if (setCurrentCategory) {
          commit(types.CATEGORY_UPD_CURRENT_CATEGORY, mainCategory)
        }
        if (populateRequestCacheTags && mainCategory && Vue.prototype.$ssrRequestContext) {
          Vue.prototype.$ssrRequestContext.output.cacheTags.add(`C${mainCategory.id}`)
        }
        if (setCurrentCategoryPath) {
          let currentPath = []
          let recurCatFinder = (category) => {
            if (!category) {
              return
            }
            if (category.parent_id) {
              dispatch('single', { key: 'id', value: category.parent_id, setCurrentCategory: false, setCurrentCategoryPath: false }).then((sc) => { // TODO: move it to the server side for one requests OR cache in indexedDb
                if (!sc) {
                  commit(types.CATEGORY_UPD_CURRENT_CATEGORY_PATH, currentPath)
                  Vue.prototype.$bus.$emit('category-after-single', { category: mainCategory })
                  return resolve(mainCategory)
                }
                currentPath.unshift(sc)
                if (sc.parent_id) {
                  recurCatFinder(sc)
                }
              }).catch(err => {
                console.error(err)
                commit(types.CATEGORY_UPD_CURRENT_CATEGORY_PATH, currentPath) // this is the case when category is not binded to the root tree - for example 'Erin Recommends'
                resolve(mainCategory)
              })
            } else {
              commit(types.CATEGORY_UPD_CURRENT_CATEGORY_PATH, currentPath)
              Vue.prototype.$bus.$emit('category-after-single', { category: mainCategory })
              resolve(mainCategory)
            }
          }
          if (typeof mainCategory !== 'undefined') {
            recurCatFinder(mainCategory) // TODO: Store breadcrumbs in IndexedDb for further usage to optimize speed?
          } else {
            reject(new Error('Category query returned empty result ' + key + ' = ' + value))
          }
        } else {
          Vue.prototype.$bus.$emit('category-after-single', { category: mainCategory })
          resolve(mainCategory)
        }
      }

      if (state.list.length > 0) { // SSR - there were some issues with using localForage, so it's the reason to use local state instead, when possible
        let category = state.list.find((itm) => { return itm[key] === value })
        // Check if category exists in the store OR we have recursively reached Default category (id=1)
        if (category || value === 1) {
          setcat(null, category)
        } else {
          reject(new Error('Category query returned empty result ' + key + ' = ' + value))
        }
      } else {
        const catCollection = Vue.prototype.$db.categoriesCollection
        // Check if category does not exist in the store AND we haven't recursively reached Default category (id=1)
        if (!catCollection.getItem(entityKeyName(key, value), setcat) && value !== 1) {
          reject(new Error('Category query returned empty result ' + key + ' = ' + value))
        }
      }
    })
  },
  /**
   * Filter category products
   */
  products (context, { populateAggregations = false, filters = [], searchProductQuery, current = 0, perPage = 50, sort = '', includeFields = null, excludeFields = null, configuration = null, append = false, skipCache = false }) {
    rootStore.state.category.current_product_query = {
      populateAggregations,
      filters,
      current,
      perPage,
      includeFields,
      excludeFields,
      configuration,
      append,
      sort
    }

    let prefetchGroupProducts = true
    if (rootStore.state.config.entities.twoStageCaching && rootStore.state.config.entities.optimize && !Vue.prototype.$isServer && !rootStore.state.twoStageCachingDisabled) { // only client side, only when two stage caching enabled
      includeFields = rootStore.state.config.entities.productListWithChildren.includeFields // we need configurable_children for filters to work
      excludeFields = rootStore.state.config.entities.productListWithChildren.excludeFields
      prefetchGroupProducts = false
      console.log('Using two stage caching for performance optimization - executing first stage product pre-fetching')
    } else {
      prefetchGroupProducts = true
      if (rootStore.state.twoStageCachingDisabled) {
        console.log('Two stage caching is disabled runtime because of no performance gain')
      } else {
        console.log('Two stage caching is disabled by the config')
      }
    }
    let t0 = new Date().getTime()

    const precachedQuery = searchProductQuery
    let productPromise = rootStore.dispatch('product/list', {
      query: precachedQuery,
      start: current,
      size: perPage,
      excludeFields: excludeFields,
      includeFields: includeFields,
      configuration: configuration,
      append: append,
      sort: sort,
      updateState: true,
      prefetchGroupProducts: prefetchGroupProducts
    }).then((res) => {
      let t1 = new Date().getTime()
      rootStore.state.twoStageCachingDelta1 = t1 - t0

      let subloaders = []
      if (!res || (res.noresults)) {
        rootStore.dispatch('notification/spawnNotification', {
          type: 'warning',
          message: i18n.t('No products synchronized for this category. Please come back while online!'),
          action1: { label: i18n.t('OK') }
        })
        if (!append) rootStore.dispatch('product/reset')
        rootStore.state.product.list = { items: [] } // no products to show TODO: refactor to rootStore.state.category.reset() and rootStore.state.product.reset()
        // rootStore.state.category.filters = { color: [], size: [], price: [] }
        return []
      } else {
        if (rootStore.state.config.products.filterUnavailableVariants && rootStore.state.config.products.configurableChildrenStockPrefetchStatic) { // prefetch the stock items
          const skus = []
          let prefetchIndex = 0
          res.items.map(i => {
            if (rootStore.state.config.products.configurableChildrenStockPrefetchStaticPrefetchCount > 0) {
              if (prefetchIndex > rootStore.state.config.products.configurableChildrenStockPrefetchStaticPrefetchCount) return
            }
            skus.push(i.sku) // main product sku to be checked anyway
            if (i.type_id === 'configurable' && i.configurable_children && i.configurable_children.length > 0) {
              for (const confChild of i.configurable_children) {
                const cachedItem = context.rootState.stock.cache[confChild.id]
                if (typeof cachedItem === 'undefined' || cachedItem === null) {
                  skus.push(confChild.sku)
                }
              }
              prefetchIndex++
            }
          })
          for (const chunkItem of chunk(skus, 15)) {
            rootStore.dispatch('stock/list', { skus: chunkItem, skipCache }) // store it in the cache
          }
        }
        if (populateAggregations === true && res.aggregations) { // populate filter aggregates
          for (let attrToFilter of filters) { // fill out the filter options
            Vue.set(rootStore.state.category.filters.available, attrToFilter, [])

            let uniqueFilterValues = new Set<string>()
            if (attrToFilter !== 'price') {
              if (res.aggregations['agg_terms_' + attrToFilter]) {
                let buckets = res.aggregations['agg_terms_' + attrToFilter].buckets
                if (res.aggregations['agg_terms_' + attrToFilter + '_options']) {
                  buckets = buckets.concat(res.aggregations['agg_terms_' + attrToFilter + '_options'].buckets)
                }

                for (let option of buckets) {
                  uniqueFilterValues.add(toString(option.key))
                }
              }

              uniqueFilterValues.forEach(key => {
                const label = optionLabel(rootStore.state.attribute, { attributeKey: attrToFilter, optionId: key })
                if (trim(label) !== '') { // is there any situation when label could be empty and we should still support it?
                  rootStore.state.category.filters.available[attrToFilter].push({
                    id: key,
                    label: label
                  })
                }
              });
            } else { // special case is range filter for prices
              const storeView = currentStoreView()
              const currencySign = storeView.i18n.currencySign
              if (res.aggregations['agg_range_' + attrToFilter]) {
                let index = 0
                let count = res.aggregations['agg_range_' + attrToFilter].buckets.length
                for (let option of res.aggregations['agg_range_' + attrToFilter].buckets) {
                  rootStore.state.category.filters.available[attrToFilter].push({
                    id: option.key,
                    from: option.from,
                    to: option.to,
                    label: (index === 0 || (index === count - 1)) ? (option.to ? '< ' + currencySign + option.to : '> ' + currencySign + option.from) : currencySign + option.from + (option.to ? ' - ' + option.to : '')// TODO: add better way for formatting, extract currency sign
                  })
                  index++
                }
              }
            }
          }
        }
      }
      return subloaders
    }).catch((err) => {
      console.error(err)
      rootStore.dispatch('notification/spawnNotification', {
        type: 'warning',
        message: i18n.t('No products synchronized for this category. Please come back while online!'),
        action1: { label: i18n.t('OK') }
      })
    })

    if (rootStore.state.config.entities.twoStageCaching && rootStore.state.config.entities.optimize && !Vue.prototype.$isServer && !rootStore.state.twoStageCachingDisabled) { // second stage - request for caching entities
      console.log('Using two stage caching for performance optimization - executing second stage product caching') // TODO: in this case we can pre-fetch products in advance getting more products than set by pageSize
      rootStore.dispatch('product/list', {
        query: precachedQuery,
        start: current,
        size: perPage,
        excludeFields: null,
        includeFields: null,
        updateState: false // not update the product listing - this request is only for caching
      }).catch((err) => {
        console.info("Problem with second stage caching - couldn't store the data")
        console.info(err)
      }).then((res) => {
        let t2 = new Date().getTime()
        rootStore.state.twoStageCachingDelta2 = t2 - t0
        console.log('Using two stage caching for performance optimization - Time comparison stage1 vs stage2', rootStore.state.twoStageCachingDelta1, rootStore.state.twoStageCachingDelta2)
        if (rootStore.state.twoStageCachingDelta1 > rootStore.state.twoStageCachingDelta2) { // two stage caching is not making any good
          rootStore.state.twoStageCachingDisabled = true
          console.log('Disabling two stage caching')
        }
      })
    }
    return productPromise
  },
  resetFilters (context) {
    context.commit(types.CATEGORY_REMOVE_FILTERS)
  },
  searchProductQuery (context, productQuery) {
    context.commit(types.CATEGORY_UPD_SEARCH_PRODUCT_QUERY, productQuery)
  }
}

export default actions
export const SN_CATEGORY = 'category'
export const CATEGORY_UPD_CATEGORIES = SN_CATEGORY + '/UPD_CATEGORIES'
export const CATEGORY_UPD_CURRENT_CATEGORY = SN_CATEGORY + '/UPD_CURRENT_CATEGORY'
export const CATEGORY_UPD_CURRENT_CATEGORY_PATH = SN_CATEGORY + '/UPD_CURRENT_CATEGORY_PATH'
export const CATEGORY_UPD_SEARCH_PRODUCT_QUERY = SN_CATEGORY + '/UPD_SEARCH_PRODUCT_QUERY'
export const CATEGORY_REMOVE_FILTERS = SN_CATEGORY + '/REMOVE_FILTERS'
import Vue from 'vue'
import { MutationTree } from 'vuex'
import * as types from './mutation-types'
import { slugify, breadCrumbRoutes } from '@vue-storefront/store/helpers'
import { entityKeyName } from '@vue-storefront/store/lib/entities'
import CategoryState from '../../types/CategoryState'
import rootStore from '@vue-storefront/store'

const mutations: MutationTree<CategoryState> = {
  [types.CATEGORY_UPD_CURRENT_CATEGORY] (state, category) {
    state.current = category
    Vue.prototype.$bus.$emit('category-after-current', { category: category })
  },
  [types.CATEGORY_UPD_CURRENT_CATEGORY_PATH] (state, path) {
    state.current_path = path // TODO: store to cache
    state.breadcrumbs.routes = breadCrumbRoutes(state.current_path)
  },
  [types.CATEGORY_UPD_CATEGORIES] (state, categories) {
    state.list = categories.items

    for (let category of state.list) {
      let catSlugSetter = (category) => {
        for (let subcat of category.children_data) { // TODO: fixme and move slug setting to vue-storefront-api
          subcat = Object.assign(subcat, { slug: (subcat.hasOwnProperty('url_key') && rootStore.state.config.products.useMagentoUrlKeys) ? subcat.url_key : (subcat.hasOwnProperty('name') ? slugify(subcat.name) + '-' + subcat.id : '') })
          catSlugSetter(subcat)
        }
      }
      catSlugSetter(category)
      const catCollection = Vue.prototype.$db.categoriesCollection
      try {
        catCollection.setItem(entityKeyName('slug', category.slug.toLowerCase()), category).catch((reason) => {
          console.error(reason) // it doesn't work on SSR
        }) // populate cache by slug
        catCollection.setItem(entityKeyName('id', category.id), category).catch((reason) => {
          console.error(reason) // it doesn't work on SSR
        }) // populate cache by id
      } catch (e) {
        console.error(e)
      }
    }
  },
  [types.CATEGORY_REMOVE_FILTERS] (state) {
    state.filters.chosen = {}
    state.current_product_query.configuration = {}
  },
  [types.CATEGORY_UPD_SEARCH_PRODUCT_QUERY] (state, newQuery) {
    state.current_product_query = newQuery
  }
}

export default mutations
import baseGetTag from './_baseGetTag.js';
import isArray from './isArray.js';
import isObjectLike from './isObjectLike.js';

/** `Object#toString` result references. */
var stringTag = '[object String]';

/**
 * Checks if `value` is classified as a `String` primitive or object.
 *
 * @static
 * @since 0.1.0
 * @memberOf _
 * @category Lang
 * @param {*} value The value to check.
 * @returns {boolean} Returns `true` if `value` is a string, else `false`.
 * @example
 *
 * _.isString('abc');
 * // => true
 *
 * _.isString(1);
 * // => false
 */
function isString(value) {
  return typeof value == 'string' ||
    (!isArray(value) && isObjectLike(value) && baseGetTag(value) == stringTag);
}

export default isString;
import Vue from 'vue'
import { ActionTree } from 'vuex'
import * as types from './mutation-types'
import rootStore from '@vue-storefront/store'
import i18n from '@vue-storefront/i18n'
import { sha3_224 } from 'js-sha3'
import { currentStoreView, localizedRoute} from '@vue-storefront/store/lib/multistore'
import omit from 'lodash-es/omit'
import RootState from '@vue-storefront/store/types/RootState'
import CartState from '../types/CartState'
import isString from 'lodash-es/isString'
import toString from 'lodash-es/toString'
import { Logger } from '@vue-storefront/core/lib/logger'
import { TaskQueue } from '@vue-storefront/core/lib/sync'
import { router } from '@vue-storefront/core/app'

const CART_PULL_INTERVAL_MS = 2000
const CART_CREATE_INTERVAL_MS = 1000
const CART_TOTALS_INTERVAL_MS = 200
const CART_METHODS_INTERVAL_MS = 1000 * 60 * 10 // refresh methods each 10 min
const MAX_BYPASS_COUNT = 10

function _updateClientItem (event, clientItem) {
  if (typeof event.result.item_id !== 'undefined') {
    rootStore.dispatch('cart/updateItem', { product: { server_item_id: event.result.item_id, sku: clientItem.sku, server_cart_id: event.result.quote_id, prev_qty: clientItem.qty } }, { root: true }) // update the server_id reference
    Vue.prototype.$bus.$emit('cart-after-itemchanged', { item: clientItem })
  }
}

function _afterServerItemUpdated (event, clientItem = null) {
  console.debug('Cart item server sync', event)
  if (clientItem === null) {
    rootStore.dispatch('cart/getItem', event.result.sku, { root: true }).then((cartItem) => {
      if (cartItem) {
        _updateClientItem(event, cartItem)
      }
    })
  } else {
    _updateClientItem(event, clientItem)
  }
}

const actions: ActionTree<CartState, RootState> = {
  serverTokenClear (context) {
    context.commit(types.CART_LOAD_CART_SERVER_TOKEN, null)
  },
  clear (context) {
    context.commit(types.CART_LOAD_CART, [])
    context.commit(types.CART_LOAD_CART_SERVER_TOKEN, null)
    if (rootStore.state.config.cart.synchronize) {
      rootStore.dispatch('cart/serverCreate', { guestCart: true }, {root: true}) // guest cart because when the order hasn't been passed to magento yet it will repopulate your cart
    }
  },
  save (context) {
    context.commit(types.CART_SAVE)
  },
  serverPull (context, { forceClientState = false, dryRun = false }) { // pull current cart FROM the server
    if (rootStore.state.config.cart.synchronize && !Vue.prototype.$isServer) {
      const newItemsHash = sha3_224(JSON.stringify({ items: context.state.cartItems, token: context.state.cartServerToken }))
      if ((Date.now() - context.state.cartServerPullAt) >= CART_PULL_INTERVAL_MS || (newItemsHash !== context.state.cartItemsHash)) {
        context.state.cartServerPullAt = Date.now()
        context.state.cartItemsHash = newItemsHash
        TaskQueue.execute({ url: rootStore.state.config.cart.pull_endpoint, // sync the cart
          payload: {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors'
          },
          silent: true,
          force_client_state: forceClientState,
          dry_run: dryRun,
          callback_event: 'store:cart/servercartAfterPulled'
        }).then(task => {
          const storeView = currentStoreView()
          if ((Date.now() - context.state.cartServerMethodsRefreshAt) >= CART_METHODS_INTERVAL_MS) {
            context.state.cartServerMethodsRefreshAt = Date.now()
            console.debug('Refreshing payment & shipping methods')
            rootStore.dispatch('cart/getPaymentMethods')
            if (context.state.cartItems.length > 0) {
              let country = rootStore.state.checkout.shippingDetails.country ? rootStore.state.checkout.shippingDetails.country : storeView.tax.defaultCountry
              rootStore.dispatch('cart/getShippingMethods', {
                country_id: country
              })
            }
          }
        })
      } else {
        console.log('Too short interval for refreshing the cart or items not changed', newItemsHash, context.state.cartItemsHash)
      }
    }
  },
  serverTotals (context, { forceClientState = false }) { // pull current cart FROM the server
    if (rootStore.state.config.cart.synchronize_totals && !Vue.prototype.$isServer) {
      if ((Date.now() - context.state.cartServerTotalsAt) >= CART_TOTALS_INTERVAL_MS) {
        context.state.cartServerPullAt = Date.now()
        TaskQueue.execute({ url: rootStore.state.config.cart.totals_endpoint, // sync the cart
          payload: {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors'
          },
          silent: true,
          force_client_state: forceClientState,
          callback_event: 'store:cart/servercartAfterTotals'
        })
      } else {
        console.log('Too short interval for refreshing the cart totals')
      }
    }
  },
  serverCreate (context, { guestCart = false }) {
    if (rootStore.state.config.cart.synchronize && !Vue.prototype.$isServer) {
      if ((Date.now() - context.state.cartServerCreatedAt) >= CART_CREATE_INTERVAL_MS) {
        const task = { url: guestCart ? rootStore.state.config.cart.create_endpoint.replace('{{token}}', '') : rootStore.state.config.cart.create_endpoint, // sync the cart
          payload: {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors'
          },
          silent: true,
          callback_event: 'store:cart/servercartAfterCreated'
        }
        TaskQueue.execute(task)
        return task
      }
    }
  },
  serverUpdateItem (context, cartItem) {
    if (!cartItem.quoteId) {
      cartItem = Object.assign(cartItem, { quoteId: context.state.cartServerToken })
    }
    return TaskQueue.execute({ url: rootStore.state.config.cart.updateitem_endpoint, // sync the cart
      payload: {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        mode: 'cors',
        body: JSON.stringify({
          cartItem: cartItem
        })
      },
      callback_event: 'store:cart/servercartAfterItemUpdated'
    }).then(task => {
      // eslint-disable-next-line no-useless-return
      if (rootStore.state.config.cart.synchronize_totals && context.state.cartItems.length > 0) {
        context.dispatch('refreshTotals')
      }
      return task
    })
  },
  serverDeleteItem (context, cartItem) {
    if (!cartItem.quoteId) {
      cartItem = Object.assign(cartItem, { quoteId: context.state.cartServerToken })
    }
    cartItem = Object.assign(cartItem, { quoteId: context.state.cartServerToken })
    return TaskQueue.execute({ url: rootStore.state.config.cart.deleteitem_endpoint, // sync the cart
      payload: {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        mode: 'cors',
        body: JSON.stringify({
          cartItem: cartItem
        })
      },
      silent: true,
      callback_event: 'store:cart/servercartAfterItemDeleted'
    }).then(task => {
      // eslint-disable-next-line no-useless-return
      if (rootStore.state.config.cart.synchronize_totals && context.state.cartItems.length > 0) {
        context.dispatch('refreshTotals')
      }
      return task
    })
  },
  load (context) {
    return new Promise((resolve, reject) => {
      if (Vue.prototype.$isServer) return
      const commit = context.commit
      const state = context.state

      if (!state.shipping || !state.shipping.method_code) {
        let shippingMethod = context.rootGetters['shipping/shippingMethods'].find(item => item.default)
        commit(types.CART_UPD_SHIPPING, shippingMethod)
      }
      if (!state.payment || !state.payment.code) {
        let paymentMethod = context.rootGetters['payment/paymentMethods'].find(item => item.default)
        commit(types.CART_UPD_PAYMENT, paymentMethod)
      }
      Vue.prototype.$db.cartsCollection.getItem('current-cart', (err, storedItems) => {
        if (err) throw new Error(err)

        if (rootStore.state.config.cart.synchronize) {
          Vue.prototype.$db.cartsCollection.getItem('current-cart-token', (err, token) => {
            if (err) throw new Error(err)
            // TODO: if token is null create cart server side and store the token!
            if (token) { // previously set token
              commit(types.CART_LOAD_CART_SERVER_TOKEN, token)
              Logger.info('Cart token received from cache.', 'cache', token)()
              Logger.info('Pulling cart from server.','cart')()
              context.dispatch('serverPull', { forceClientState: false, dryRun: !rootStore.state.config.cart.server_merge_by_default })
            } else {
              Logger.info('Creating server cart token', 'cart')()
              context.dispatch('serverCreate', { guestCart: false })
            }
          })
        }
        commit(types.CART_LOAD_CART, storedItems)
        resolve(storedItems)
      })
    })
  },
  // This should be a getter, just sayin
  getItem ({ commit, dispatch, state }, sku) {
    return state.cartItems.find(p => p.sku === sku)
  },
  goToCheckout (context) {
    router.push(localizedRoute('/checkout', currentStoreView().storeCode))
  },
  addItem ({ commit, dispatch, state }, { productToAdd, forceServerSilence = false }) {
    let productsToAdd = []
    if (productToAdd.type_id === 'grouped') { // TODO: add bundle support
      productsToAdd = productToAdd.product_links.filter((pl) => { return pl.link_type === 'associated' }).map((pl) => { return pl.product })
    } else {
      productsToAdd.push(productToAdd)
    }
    let productHasBeenAdded = false
    let productIndex = 0
    for (let product of productsToAdd) {
      if (typeof product === 'undefined' || product === null) continue
      if (product.priceInclTax <= 0) {
        rootStore.dispatch('notification/spawnNotification', {
          type: 'error',
          message: i18n.t('Product price is unknown, product cannot be added to the cart!'),
          action1: { label: i18n.t('OK') }
        })
        continue
      }
      if (rootStore.state.config.entities.optimize && rootStore.state.config.entities.optimizeShoppingCart) {
        product = omit(product, ['configurable_children', 'configurable_options', 'media_gallery', 'description', 'category', 'category_ids', 'product_links', 'stock', 'description'])
      }
      if (product.errors !== null && typeof product.errors !== 'undefined') {
        let productCanBeAdded = true
        for (let errKey in product.errors) {
          if (product.errors[errKey]) {
            productCanBeAdded = false
            rootStore.dispatch('notification/spawnNotification', {
              type: 'error',
              message: product.errors[errKey],
              action1: { label: i18n.t('OK') }
            })
          }
        }
        if (!productCanBeAdded) {
          continue
        }
      }
      const record = state.cartItems.find(p => p.sku === product.sku)
      dispatch('stock/check', { product: product, qty: record ? record.qty + 1 : (product.qty ? product.qty : 1) }, {root: true}).then(result => {
        product.onlineStockCheckid = result.onlineCheckTaskId // used to get the online check result
        if (result.status === 'volatile') {
          rootStore.dispatch('notification/spawnNotification', {
            type: 'warning',
            message: i18n.t('The system is not sure about the stock quantity (volatile). Product has been added to the cart for pre-reservation.'),
            action1: { label: i18n.t('OK') }
          })
        }
        if (result.status === 'out_of_stock') {
          rootStore.dispatch('notification/spawnNotification', {
            type: 'error',
            message: i18n.t('The product is out of stock and cannot be added to the cart!'),
            action1: { label: i18n.t('OK') }
          })
        }
        if (result.status === 'ok' || result.status === 'volatile') {
          commit(types.CART_ADD_ITEM, { product })
          productHasBeenAdded = true
        }
        if (productIndex === (productsToAdd.length - 1) && productHasBeenAdded) {
          let notificationData = {
            type: 'success',
            message: i18n.t('Product has been added to the cart!'),
            action1: { label: i18n.t('OK') },
            action2: null
          }
          if (!rootStore.state.config.externalCheckout) { // if there is externalCheckout enabled we don't offer action to go to checkout as it can generate cart desync
            notificationData.action2 = { label: i18n.t('Proceed to checkout'), action: () => {
              dispatch('goToCheckout')
            }}
          }
          if (rootStore.state.config.cart.synchronize && !forceServerSilence) {
            dispatch('serverPull', { forceClientState: true })
          } else {
            rootStore.dispatch('notification/spawnNotification', notificationData)
          }
        }
        productIndex++
      })
    }
  },
  removeItem ({ commit, dispatch }, payload) {
    let removeByParentSku = true // backward compatibility call format
    let product = payload
    if(payload.product) { // new call format since 1.4
      product = payload.product
      removeByParentSku = payload.removeByParentSku
    }
    commit(types.CART_DEL_ITEM, { product, removeByParentSku })
    if (rootStore.state.config.cart.synchronize && product.server_item_id) {
      dispatch('serverPull', { forceClientState: true })
    }
  },
  removeNonConfirmedVariants ({ commit, dispatch }, payload) {
    let removeByParentSku = true // backward compatibility call format
    let product = payload
    if(payload.product) { // new call format since 1.4
      product = payload.product
      removeByParentSku = payload.removeByParentSku
    }
    commit(types.CART_DEL_NON_CONFIRMED_ITEM, { product })
    if (rootStore.state.config.cart.synchronize && product.server_item_id) {
      dispatch('serverPull', { forceClientState: true })
    }
  },
  updateQuantity ({ commit, dispatch }, { product, qty, forceServerSilence = false }) {
    commit(types.CART_UPD_ITEM, { product, qty })
    if (rootStore.state.config.cart.synchronize && product.server_item_id && !forceServerSilence) {
      dispatch('serverPull', { forceClientState: true })
    }
  },
  updateItem ({ commit }, { product }) {
    commit(types.CART_UPD_ITEM_PROPS, { product })
  },
  getPaymentMethods (context) {
    if (rootStore.state.config.cart.synchronize_totals && (typeof navigator !== 'undefined' ? navigator.onLine : true)) {
      TaskQueue.execute({ url: rootStore.state.config.cart.paymentmethods_endpoint,
        payload: {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
          mode: 'cors'
        },
        silent: true
      }).then((task: any) => {
        let backendMethods = task.result
        let paymentMethods = context.rootGetters['payment/paymentMethods'].slice(0).filter((itm) => {
          return (typeof itm !== 'object' || !itm.is_server_method)
        }) // copy
        let uniqueBackendMethods = []
        for (let i = 0; i < backendMethods.length; i++) {
          if (typeof backendMethods[i] === 'object' && !paymentMethods.find(item => item.code === backendMethods[i].code)) {
            backendMethods[i].is_server_method = true
            paymentMethods.push(backendMethods[i])
            uniqueBackendMethods.push(backendMethods[i])
          }
        }
        rootStore.dispatch('payment/replaceMethods', paymentMethods, { root: true })
        Vue.prototype.$bus.$emit('set-unique-payment-methods', uniqueBackendMethods)
      }).catch(e => {
        console.error(e)
      })
    }
  },
  getShippingMethods (context, address) {
    if (rootStore.state.config.cart.synchronize_totals && (typeof navigator !== 'undefined' ? navigator.onLine : true)) {
      TaskQueue.execute({ url: rootStore.state.config.cart.shippingmethods_endpoint,
        payload: {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          mode: 'cors',
          body: JSON.stringify({
            address: address
          })
        },
        silent: true
      }).then((task: any) => {
        if (task.result.length > 0) {
          rootStore.dispatch('shipping/replaceMethods', task.result, { root: true })
        }
      }).catch(e => {
        console.error(e)
      })
    }
  },
  refreshTotals (context, methodsData) {
    const storeView = currentStoreView()
    if (rootStore.state.config.cart.synchronize_totals) {
      if (!methodsData) {
        let country = rootStore.state.checkout.shippingDetails.country ? rootStore.state.checkout.shippingDetails.country : storeView.tax.defaultCountry
        const shippingMethods = context.rootGetters['shipping/shippingMethods']
        const paymentMethods = context.rootGetters['payment/paymentMethods']
        let shipping = shippingMethods ? shippingMethods.find(item => item.default) : null
        let payment = paymentMethods ? paymentMethods.find(item => item.default) : null
        if (!shipping && shippingMethods && shippingMethods.length > 0) {
          shipping = shippingMethods[0]
        }
        if (!payment && paymentMethods && paymentMethods.length > 0) {
          payment = paymentMethods[0]
        }
        methodsData = {
          country: country,
          method_code: shipping ? shipping.method_code : null,
          carrier_code: shipping ? shipping.carrier_code : null,
          payment_method: payment.code
        }
      }
      if (methodsData.country && methodsData.carrier_code) {
        TaskQueue.execute({ url: rootStore.state.config.cart.shippinginfo_endpoint,
          payload: {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors',
            body: JSON.stringify({
              addressInformation: {
                shippingAddress: {
                  countryId: methodsData.country
                },
                shippingCarrierCode: methodsData.carrier_code,
                shippingMethodCode: methodsData.method_code
              }
            })
          },
          silent: true,
          callback_event: 'store:cart/servercartAfterTotals'
        }).catch(e => {
          console.error(e)
        })
      } else {
        context.dispatch('cart/serverTotals', {}, { root: true })
      }
    }
  },
  removeCoupon (context) {
    return new Promise((resolve, reject) => {
      if (rootStore.state.config.cart.synchronize_totals && (typeof navigator !== 'undefined' ? navigator.onLine : true)) {
        TaskQueue.execute({ url: rootStore.state.config.cart.deletecoupon_endpoint,
          payload: {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors'
          },
          silent: true
        }).then((task : any) => {
          if (task.result) {
            context.dispatch('refreshTotals')
            resolve(task.result)
          }
        }).catch(e => {
          console.error(e)
          reject(e)
        })
      }
    });
  },
  applyCoupon (context, couponCode) {
    return new Promise((resolve, reject) => {
      if (rootStore.state.config.cart.synchronize_totals && (typeof navigator !== 'undefined' ? navigator.onLine : true)) {
        TaskQueue.execute({ url: rootStore.state.config.cart.applycoupon_endpoint.replace('{{coupon}}', couponCode),
          payload: {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors'
          },
          silent: true
        }).then((task:any) => {
          if (task.result === true) {
            context.dispatch('refreshTotals')
            resolve(task.result)
          } else {
            reject(false)
          }
        }).catch(e => {
          console.log(e)
          reject(e)
        })
      }
    })
  },
  userAfterLoggedin () {
    Vue.prototype.$db.usersCollection.getItem('last-cart-bypass-ts', (err, lastCartBypassTs) => {
      if (err) {
        console.error(err)
      }
      if (!rootStore.state.config.cart.bypassCartLoaderForAuthorizedUsers || (Date.now() - lastCartBypassTs) >= (1000 * 60 * 24)) { // don't refresh the shopping cart id up to 24h after last order
        rootStore.dispatch('cart/serverCreate', { guestCart: false }, { root: true })
      }
    })
  },
  servercartAfterCreated (context, event) {
    const cartToken = event.result
    if (event.resultCode === 200) {
      Logger.info('Server cart token created.', 'cart', cartToken)()
      rootStore.commit(types.SN_CART + '/' + types.CART_LOAD_CART_SERVER_TOKEN, cartToken)
      rootStore.dispatch('cart/serverPull', { forceClientState: false, dryRun: !rootStore.state.config.cart.server_merge_by_default }, { root: true })
    } else {
      let resultString = event.result ? toString(event.result) : null
      if (resultString && (resultString.indexOf(i18n.t('not authorized')) < 0 && resultString.indexOf('not authorized')) < 0) { // not respond to unathorized errors here
        if (rootStore.state.cart.bypassCount < MAX_BYPASS_COUNT) {
          console.log('Bypassing with guest cart', rootStore.state.cart.bypassCount)
          rootStore.state.cart.bypassCount = rootStore.state.cart.bypassCount + 1
          rootStore.dispatch('cart/serverCreate', { guestCart: true }, { root: true })
          console.error(event.result)
        }
      }
    }
  },
  servercartAfterTotals (context, event) {
    if (event.resultCode === 200) {
      const totalsObj = event.result.totals ? event.result.totals : event.result
      Logger.info('Overriding server totals. ', 'cart', totalsObj)()
      let itemsAfterTotal = {}
      let platformTotalSegments = totalsObj.total_segments
      for (let item of totalsObj.items) {
        if (item.options && isString(item.options)) item.options = JSON.parse(item.options)
        itemsAfterTotal[item.item_id] = item
        rootStore.dispatch('cart/updateItem', { product: { server_item_id: item.item_id, totals: item, qty: item.qty } }, { root: true }) // update the server_id reference
      }
      rootStore.commit(types.SN_CART + '/' + types.CART_UPD_TOTALS, { itemsAfterTotal: itemsAfterTotal, totals: totalsObj, platformTotalSegments: platformTotalSegments })
    } else {
      console.error(event.result)
    }
  },
  servercartAfterPulled (context, event) {
    if (event.resultCode === 200) {
      let diffLog = []
      let serverCartUpdateRequired = false
      let clientCartUpdateRequired = false
      let cartHasItems = false
      const serverItems = event.result
      const clientItems = rootStore.state.cart.cartItems
      for (const clientItem of clientItems) {
        cartHasItems = true
        const serverItem = serverItems.find((itm) => {
          return itm.sku === clientItem.sku || itm.sku.indexOf(clientItem.sku + '-') === 0 /* bundle products */
        })

        if (!serverItem) {
          Logger.warn('No server item with sku ' + clientItem.sku + ' on stock.', 'cart')
          diffLog.push({ 'party': 'server', 'sku': clientItem.sku, 'status': 'no_item' })
          if (!event.dry_run) {
            rootStore.dispatch('cart/serverUpdateItem', {
              sku: clientItem.parentSku && rootStore.state.config.cart.setConfigurableProductOptions ? clientItem.parentSku : clientItem.sku,
              qty: clientItem.qty,
              product_option: clientItem.product_option
            }, { root: true }).then((event) => {
              _afterServerItemUpdated(event, clientItem)
            })
            serverCartUpdateRequired = true
          }
        } else if (serverItem.qty !== clientItem.qty) {
          console.log('Wrong qty for ' + clientItem.sku, clientItem.qty, serverItem.qty)
          diffLog.push({ 'party': 'server', 'sku': clientItem.sku, 'status': 'wrong_qty', 'client_qty': clientItem.qty, 'server_qty': serverItem.qty })
          if (!event.dry_run) {
            rootStore.dispatch('cart/serverUpdateItem', {
              sku: clientItem.parentSku && rootStore.state.config.cart.setConfigurableProductOptions ? clientItem.parentSku : clientItem.sku,
              qty: clientItem.qty,
              item_id: serverItem.item_id,
              quoteId: serverItem.quote_id,
              product_option: clientItem.product_option
            }, { root: true }).then((event) => {
              _afterServerItemUpdated(event, clientItem)
            })
            serverCartUpdateRequired = true
          }
        } else {
          Logger.info('Server and client item with SKU ' + clientItem.sku + ' synced. Updating cart.', 'cart')()
          // console.log('Updating server id to ', { sku: clientItem.sku, server_cart_id: serverItem.quote_id, server_item_id: serverItem.item_id, product_option: serverItem.product_option })
          if (!event.dry_run) {
            rootStore.dispatch('cart/updateItem', { product: { sku: clientItem.sku, server_cart_id: serverItem.quote_id, server_item_id: serverItem.item_id, product_option: serverItem.product_option } }, { root: true })
          }
        }
      }

      for (const serverItem of serverItems) {
        if (serverItem) {
          const clientItem = clientItems.find((itm) => {
            return itm.sku === serverItem.sku || serverItem.sku.indexOf(itm.sku + '-') === 0 /* bundle products */
          })
          if (!clientItem) {
            Logger.info('No client item for' + serverItem.sku, 'cart')()
            diffLog.push({ 'party': 'client', 'sku': serverItem.sku, 'status': 'no_item' })

            if (!event.dry_run) {
              if (event.force_client_state) {
                Logger.info('Removing product from cart', 'cart', serverItem)()

                console.log('Removing item', serverItem.sku, serverItem.item_id)
                serverCartUpdateRequired = true
                rootStore.dispatch('cart/serverDeleteItem', {
                  sku: serverItem.sku,
                  item_id: serverItem.item_id,
                  quoteId: serverItem.quote_id
                }, { root: true })
              } else {
                clientCartUpdateRequired = true
                cartHasItems = true
                rootStore.dispatch('product/single', { options: { sku: serverItem.sku }, setCurrentProduct: false, selectDefaultVariant: false }).then((product) => {
                  product.server_item_id = serverItem.item_id
                  product.qty = serverItem.qty
                  product.server_cart_id = serverItem.quote_id
                  if (serverItem.product_option) {
                    product.product_option = serverItem.product_option
                  }
                  rootStore.dispatch('cart/addItem', { productToAdd: product, forceServerSilence: true }).then(() => {
                  // rootStore.dispatch('cart/updateItem', { product: product })
                  })
                })
              }
            }
          }
        }
      }

      if (!event.dry_run) {
        if ((!serverCartUpdateRequired || clientCartUpdateRequired) && cartHasItems) {
          rootStore.dispatch('cart/refreshTotals')
        }
      }
      Vue.prototype.$bus.$emit('servercart-after-diff', { diffLog: diffLog, serverItems: serverItems, clientItems: clientItems, dryRun: event.dry_run, event: event }) // send the difflog
       Logger.info('Client/Server cart synchronised ', 'cart', diffLog)()
    } else {
      console.error(event.result) // override with guest cart
      if (rootStore.state.cart.bypassCount < MAX_BYPASS_COUNT) {
        console.log('Bypassing with guest cart', rootStore.state.cart.bypassCount)
        rootStore.state.cart.bypassCount = rootStore.state.cart.bypassCount + 1
        rootStore.dispatch('cart/serverCreate', { guestCart: true }, { root: true })
        console.error(event.result)
      }
    }
  },
  servercartAfterItemUpdated (context, event) {
    const originalCartItem = JSON.parse(event.payload.body).cartItem
    if (event.resultCode !== 200) {
      // TODO: add the strategy to configure behaviour if the product is (confirmed) out of the stock
      if (originalCartItem.item_id) {
        rootStore.dispatch('cart/getItem', originalCartItem.sku, { root: true }).then((cartItem) => {
          if (cartItem) {
            console.log('Restoring qty after error', originalCartItem.sku, cartItem.prev_qty)
            if (cartItem.prev_qty > 0) {
              rootStore.dispatch('cart/updateItem', { product: { qty: cartItem.prev_qty } }, { root: true }) // update the server_id reference
              Vue.prototype.$bus.$emit('cart-after-itemchanged', { item: cartItem })
            } else {
              rootStore.dispatch('cart/removeItem', { product: cartItem, removeByParentSku: false }, { root: true }) // update the server_id reference
            }
          }
        })
      } else {
        Logger.warn('Removing product from cart', 'cart', originalCartItem)()
        rootStore.commit('cart/' + types.CART_DEL_NON_CONFIRMED_ITEM, { product: originalCartItem }, {root: true})
      }
    } else {
      const isThisNewItemAddedToTheCart = (!originalCartItem || !originalCartItem.item_id)
      if (isThisNewItemAddedToTheCart) {
        let notificationData = {
          type: 'success',
          message: i18n.t('Product has been added to the cart!'),
          action1: { label: i18n.t('OK') },
          action2: null
        }
        if (!rootStore.state.config.externalCheckout) { // if there is externalCheckout enabled we don't offer action to go to checkout as it can generate cart desync
          notificationData.action2 = { label: i18n.t('Proceed to checkout'), action: () => {
            context.dispatch('goToCheckout')
          }}
        }
        rootStore.dispatch('notification/spawnNotification', notificationData)
      }
    }
  },
  toggleMicrocart ({ commit }) {
    commit(types.CART_TOGGLE_MICROCART)
  },
  servercartAfterItemDeleted (context, event) {
  }
}

